// At the top of your JavaScript in sales-orders.html
document.addEventListener('DOMContentLoaded', () => {
  if (typeof anime === 'undefined') {
    console.error('Anime.js not loaded. Loading fallback...');
    const script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/animejs@3.2.1/lib/anime.min.js';
    script.async = true;
    script.onerror = () => console.error('Failed to load anime.js');
    script.onload = () => console.log('Anime.js loaded successfully');
    document.head.appendChild(script);
  }
});